INSERT INTO `idcards_template` VALUES ('7', 'Basic Card Reprint', 'Reprint', 'Template to reprint Patient ID Cards for existing patients.', '<?xml version=\"1.0\"?>\r\n<idCardList>\r\n    <!-- Important to keep baseURL element on one line with no spaces. -->\r\n    <baseURL>$!{baseURL}</baseURL>\r\n    <cardName>ID Card</cardName>\r\n#foreach($patientId in $cohort.memberIds)\r\n$!{fn.setPatientId($patientId)}\r\n<idCard>\r\n<id>$!{fn.getPatientAttr(\'PatientIdentifier\', \'identifier\')}</id>\r\n<name>\r\n$!{fn.getPatientAttr(\'PersonName\', \'givenName\')}\r\n$!{fn.getPatientAttr(\'PersonName\', \'middleName\')}\r\n$!{fn.getPatientAttr(\'PersonName\', \'familyName\')}\r\n</name>\r\n<gender>$!{fn.getPatientAttr(\'Person\', \'gender\')}</gender>\r\n#set($birthdate = $!{fn.getPatientAttr(\'Person\', \'birthdate\')})\r\n#set($birthdate_estimated = $!{fn.getPatientAttr(\'Person\', \'birthdateEstimated\')})\r\n#set($age = $!{fn.getAge(${birthdate})})\r\n#if ($!{age.get(1)} == \"days\") #set($age_text = \"$!{age.get(0)} day-old\")\r\n#else\r\n#if ($!{age.get(1)} == \"months\") #set($age_text = \"$!{age.get(0)} month-old\")\r\n#else #set($age_text = \"$!{age.get(0)} year-old\")\r\n#end\r\n#end\r\n<birthdate estimate=\"${birthdate_estimated}\" age=\"$!{age_text}\">\r\n$!{fn.formatDate(\'dd-MMM-yyyy\',${birthdate})}\r\n</birthdate>\r\n</idCard>\r\n#end \r\n</idCardList>', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\r\n<xsl:stylesheet version=\"2.0\"\r\n      xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\"\r\n      xmlns:fo=\"http://www.w3.org/1999/XSL/Format\"\r\n      xmlns:fn=\"http://www.w3.org/2005/02/xpath-functions\">\r\n  <xsl:output method=\"xml\" indent=\"yes\"/>\r\n  <xsl:strip-space elements=\"*\" />\r\n\r\n  <xsl:template match=\"/\">\r\n    <fo:root>\r\n      <fo:layout-master-set>\r\n            <fo:simple-page-master master-name=\"simple\" page-height=\"297mm\" page-width=\"210mm\"\r\n                margin-top=\"1cm\" margin-left=\"1cm\" margin-right=\"1cm\" margin-bottom=\"1cm\">\r\n                <fo:region-body margin-top=\"0.8cm\" margin-bottom=\"1.2cm\" />\r\n        </fo:simple-page-master>\r\n      </fo:layout-master-set>\r\n      <fo:page-sequence master-reference=\"simple\">\r\n        <fo:flow flow-name=\"xsl-region-body\" font-family=\"Helvetica\" font-size=\"8pt\">\r\n            <fo:block text-align=\"center\">\r\n                <fo:table table-layout=\"fixed\" start-indent=\"(100% - 160mm) div 2\"\r\n                        table-omit-header-at-break=\"false\" border=\"2 pt solid black\">\r\n                    <fo:table-column column-width=\"80mm\"/>\r\n                    <fo:table-column column-width=\"80mm\"/>\r\n\r\n                    <fo:table-header border=\"solid white\">\r\n                        <fo:table-row height=\"2mm\" >\r\n                            <fo:table-cell>\r\n                                <fo:block font-size=\"6pt\" text-align=\"left\">\r\n                                    DRAFT Patient ID Cards <xsl:value-of select=\"idCardList/baseURL\"/>\r\n                                </fo:block>\r\n                            </fo:table-cell>\r\n                            <fo:table-cell>\r\n                                <fo:block font-size=\"6pt\" text-align=\"center\">\r\n                                    Page: <fo:page-number/>\r\n                                </fo:block>\r\n                            </fo:table-cell>\r\n                        </fo:table-row>\r\n                    </fo:table-header>\r\n\r\n                    <fo:table-body>\r\n                        <xsl:variable name=\"baseURL\" select=\"idCardList/baseURL\"/>\r\n                        <xsl:variable name=\"cardName\" select=\"idCardList/cardName\"/>\r\n                        <xsl:for-each select=\"idCardList/idCard\">\r\n                            <fo:table-row height=\"50mm\" border=\"2pt solid black\">\r\n                                    <xsl:variable name=\"identifier\" select=\"./id\"/>\r\n                                    <fo:table-cell border=\"1pt dashed grey\" display-align=\"center\">\r\n                                        <fo:block text-align=\"center\" start-indent=\"0mm\">\r\n                                            <fo:block-container>\r\n                                                <fo:block-container position=\"absolute\">\r\n                                                    <fo:block>\r\n                                                        <fo:instream-foreign-object >\r\n                                                            <barcode:barcode\r\n                                                                xmlns:barcode=\"http://barcode4j.krysalis.org/ns\"\r\n                                                                message=\"{$identifier}\" orientation=\"0\">\r\n                                                                <barcode:code39>\r\n                                                                    <barcode:height>20mm</barcode:height>\r\n                                                                </barcode:code39>\r\n                                                            </barcode:barcode>\r\n                                                        </fo:instream-foreign-object>\r\n                                                    </fo:block>\r\n                                                </fo:block-container>\r\n                                                <fo:block-container position=\"relative\">\r\n                                                    <fo:block font-weight=\"normal\"\r\n                                                              text-align=\"left\"\r\n                                                              display-align=\"center\"\r\n                                                              color=\"blue\"\r\n                                                              font-size=\"10mm\">\r\n                                                        <fo:table table-layout=\"fixed\">\r\n                                                            <fo:table-column column-width=\"25mm\"/>\r\n                                                            <fo:table-column column-width=\"45mm\"/>\r\n                                                            <fo:table-column column-width=\"20mm\"/>\r\n                                                            <fo:table-body>\r\n                                                                <fo:table-row>\r\n                                                                    <fo:table-cell>\r\n                                                                        <fo:block text-align=\"center\">\r\n                                                                            <fo:external-graphic\r\n                                                                                    src=\"url(\'{$baseURL}/moduleResources/idcards/card_logo_01.png\')\"\r\n                                                                                    scaling=\"uniform\"\r\n                                                                                    content-height=\"20mm\"/>\r\n                                                                        </fo:block>\r\n                                                                    </fo:table-cell>\r\n                                                                    <fo:table-cell>\r\n                                                                        <fo:block text-align=\"right\">\r\n                                                                            <fo:external-graphic\r\n                                                                                    src=\"url(\'{$baseURL}/moduleResources/idcards/draft.png\')\"\r\n                                                                                    scaling=\"uniform\"\r\n                                                                                    content-width=\"15mm\"/>\r\n                                                                        </fo:block>\r\n                                                                    </fo:table-cell>\r\n                                                                    <fo:table-cell>\r\n                                                                        <fo:block>\r\n                                                                        </fo:block>\r\n                                                                    </fo:table-cell>\r\n                                                                </fo:table-row>\r\n                                                            </fo:table-body>\r\n                                                        </fo:table>\r\n                                                    </fo:block>\r\n                                                </fo:block-container>\r\n                                            </fo:block-container>\r\n                                        </fo:block>\r\n                                    </fo:table-cell>\r\n                                    <fo:table-cell border=\"1pt dashed grey\" display-align=\"center\">\r\n                                        <fo:block text-align=\"center\" start-indent=\"0mm\">\r\n                                            <fo:block-container>\r\n                                                <fo:block-container position=\"absolute\">\r\n                                                    <fo:block>\r\n                                                        <fo:table table-layout=\"fixed\">\r\n                                                            <fo:table-column column-width=\"100%\"/>\r\n                                                            <fo:table-body>\r\n                                                                <fo:table-row>\r\n                                                                    <fo:table-cell>\r\n                                                                        <fo:block font-weight=\"bold\" font-size=\"large\">\r\n                                                                            <xsl:value-of select=\"$cardName\"/>\r\n                                                                        </fo:block>\r\n                                                                        <fo:block >\r\n                                                                            <xsl:value-of select=\"name\"/>\r\n                                                                        </fo:block>\r\n                                                                        <fo:block>\r\n                                                                            <xsl:value-of select=\"id\"/>\r\n                                                                        </fo:block>\r\n                                                                        <fo:block>\r\n                                                                            <xsl:value-of select=\"gender\"/>\r\n                                                                        </fo:block>\r\n                                                                        <fo:block>\r\n                                                                            <xsl:value-of select=\"birthdate\"/>\r\n                                                                        </fo:block>\r\n                                                                    </fo:table-cell>\r\n                                                                </fo:table-row>\r\n                                                            </fo:table-body>\r\n                                                        </fo:table>\r\n                                                    </fo:block>\r\n                                                </fo:block-container>\r\n                                                <fo:block-container position=\"relative\">\r\n                                                    <fo:block font-weight=\"normal\"\r\n                                                              text-align=\"left\"\r\n                                                              display-align=\"center\"\r\n                                                              color=\"blue\"\r\n                                                              font-size=\"10mm\">\r\n                                                        <fo:table table-layout=\"fixed\">\r\n                                                            <fo:table-column column-width=\"25mm\"/>\r\n                                                            <fo:table-column column-width=\"45mm\"/>\r\n                                                            <fo:table-column column-width=\"20mm\"/>\r\n                                                            <fo:table-body>\r\n                                                                <fo:table-row>\r\n                                                                    <fo:table-cell>\r\n                                                                        <fo:block text-align=\"center\">\r\n                                                                            <fo:external-graphic\r\n                                                                                    src=\"url(\'{$baseURL}/moduleResources/idcards/card_logo_01.png\')\"\r\n                                                                                    scaling=\"uniform\"\r\n                                                                                    content-height=\"20mm\"/>\r\n                                                                        </fo:block>\r\n                                                                    </fo:table-cell>\r\n                                                                    <fo:table-cell>\r\n                                                                        <fo:block text-align=\"right\">\r\n                                                                            <fo:external-graphic\r\n                                                                                    src=\"url(\'{$baseURL}/moduleResources/idcards/draft.png\')\"\r\n                                                                                    scaling=\"uniform\"\r\n                                                                                    content-width=\"15mm\"/>\r\n                                                                        </fo:block>\r\n                                                                    </fo:table-cell>\r\n                                                                    <fo:table-cell>\r\n                                                                        <fo:block>\r\n                                                                        </fo:block>\r\n                                                                    </fo:table-cell>\r\n                                                                </fo:table-row>\r\n                                                            </fo:table-body>\r\n                                                        </fo:table>\r\n                                                    </fo:block>\r\n                                                </fo:block-container>\r\n                                            </fo:block-container>\r\n                                        </fo:block>\r\n                                    </fo:table-cell>\r\n                            </fo:table-row>\r\n                        </xsl:for-each>\r\n                    </fo:table-body>\r\n                </fo:table>\r\n            </fo:block>\r\n        </fo:flow>\r\n      </fo:page-sequence>\r\n    </fo:root>\r\n  </xsl:template>\r\n</xsl:stylesheet>', '1', '2008-11-11 11:55:36', '1', '2009-05-18 14:27:22');
INSERT INTO `idcards_template` VALUES ('8', 'OpenMRS Card New/Empty (Front)', 'Print New Identifiers', 'Template to print ID Cards with newly created Patient Identifiers.  This is printed onto the paper returned from the print-shop that has the blank fields on the back. ', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\r\n<doc>\r\n    <!-- Important to keep baseURL element on one line with no spaces. -->\r\n    <baseURL>$!{baseURL}</baseURL>\r\n    <cardName>OpenMRS ID Card</cardName>\r\n    <upperLeft>Date Issued</upperLeft>\r\n    <upperRight>Assigning Location</upperRight>\r\n    <page>\r\n        #foreach ($elem in $identifiers)\r\n            #if ($velocityCount < $identifiers.size())\r\n                #set ($mod = $velocityCount % 2)\r\n                <!-- print two columns -->\r\n                #if ($mod == 1)\r\n                    #set ($left = $velocityCount - 1)\r\n                    <row>\r\n                        <col>${identifiers.get($left)}</col>\r\n                        <col>${identifiers.get($velocityCount)}</col>\r\n                    </row>\r\n                #else\r\n                    <!-- print one column for the last element -->\r\n                    #if ($velocityCount == $identifiers.size() - 1)\r\n                        #set ($last = $identifiers.size() - 1)\r\n                        <row>\r\n                            <col>${identifiers.get($last)}</col>\r\n                        </row>\r\n                    #end\r\n                #end\r\n            #end\r\n        #end\r\n    </page>\r\n</doc>', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\r\n<xsl:stylesheet version=\"2.0\"\r\n      xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\"\r\n      xmlns:fo=\"http://www.w3.org/1999/XSL/Format\"\r\n      xmlns:fn=\"http://www.w3.org/2005/02/xpath-functions\">\r\n  <xsl:output method=\"xml\" indent=\"yes\"/>\r\n\r\n  <xsl:template match=\"/\">\r\n    <fo:root>\r\n      <fo:layout-master-set>\r\n        <fo:simple-page-master master-name=\"simple\" page-height=\"297mm\" page-width=\"210mm\"\r\n            margin-top=\"0.6cm\" margin-left=\"0cm\" margin-right=\"0cm\" margin-bottom=\"0.8cm\">\r\n            <fo:region-body margin-top=\"0.4cm\" margin-bottom=\"0cm\" />\r\n        </fo:simple-page-master>\r\n      </fo:layout-master-set>\r\n      <fo:page-sequence master-reference=\"simple\">\r\n        <fo:flow flow-name=\"xsl-region-body\" font-family=\"Helvetica\" font-size=\"8pt\">\r\n            <fo:block text-align=\"center\">\r\n                <fo:table table-layout=\"fixed\" start-indent=\"(100% - 168mm - 3mm) div 2\"\r\n                        table-omit-header-at-break=\"false\">\r\n                    <fo:table-column column-width=\"84mm\"/>\r\n                    <fo:table-column column-width=\"84mm\"/>\r\n\r\n                    <fo:table-header border=\"solid white\" >\r\n                        <fo:table-row height=\"6mm\" >\r\n                            <fo:table-cell>\r\n                                <fo:block font-size=\"6pt\" font-weight=\"bold\" display-align=\"before\">\r\n                                    <xsl:value-of select=\"doc/cardName\"/> (<xsl:value-of select=\"doc/baseURL\"/>)\r\n                                </fo:block>\r\n                            </fo:table-cell>\r\n                            <fo:table-cell>\r\n                                <fo:block font-size=\"6pt\" font-weight=\"bold\">\r\n                                    Page: <fo:page-number/>\r\n                                </fo:block>\r\n                            </fo:table-cell>\r\n                        </fo:table-row>\r\n                    </fo:table-header>\r\n\r\n                    <fo:table-body>\r\n                        <xsl:variable name=\"baseURL\" select=\"doc/baseURL\"/>\r\n                        <xsl:variable name=\"upperLeft\" select=\"doc/upperLeft\"/>\r\n                        <xsl:variable name=\"upperRight\" select=\"doc/upperRight\"/>\r\n                        <!-- xsl:variable name=\"contextPath\" select=\"idCardList/contextPath\" -->\r\n                        <xsl:variable name=\"padding\" select=\"\'000000000\'\"/>\r\n                        <xsl:for-each select=\"doc/page/row\">\r\n                            <fo:table-row height=\"52mm\">\r\n                                <xsl:for-each select=\"col\">\r\n                                    <xsl:variable name=\"id\" select=\".\"/>\r\n                                    <xsl:variable name=\"identifier\" select=\"substring(concat($padding, $id), string-length($id) - 1, string-length($padding) + 2)\" />\r\n                                    <fo:table-cell border=\"2pt solid black\" display-align=\"before\">\r\n                                        <fo:block text-align=\"center\" start-indent=\"0mm\" margin-top=\"0mm\">\r\n                                            <fo:block-container>\r\n                                                <fo:block-container position=\"absolute\">\r\n                                                    <fo:block font-size=\"6pt\" font-weight=\"bold\">\r\n                                                        <fo:table table-layout=\"fixed\" >\r\n                                                            <fo:table-column column-width=\"40%\"/>\r\n                                                            <fo:table-column column-width=\"20%\"/>\r\n                                                            <fo:table-column column-width=\"40%\"/>\r\n                                                            <fo:table-body>\r\n                                                                <fo:table-row height=\"6mm\" >\r\n                                                                    <fo:table-cell><fo:block/></fo:table-cell>\r\n                                                                </fo:table-row>\r\n                                                                <fo:table-row >\r\n                                                                    <fo:table-cell color=\"black\"\r\n                                                                            font-weight=\"bold\"\r\n                                                                            font-size=\"6pt\"\r\n                                                                            font-style=\"italic\"\r\n                                                                            border-top-color=\"black\"\r\n                                                                            border-top-style=\"dotted\"\r\n                                                                            border-top-width=\"1pt\">\r\n                                                                        <fo:block linefeed-treatment=\"preserve\"><xsl:value-of select=\"$upperLeft\"/></fo:block>\r\n                                                                    </fo:table-cell>\r\n                                                                    <fo:table-cell >\r\n                                                                            <fo:block></fo:block>\r\n                                                                        </fo:table-cell>\r\n                                                                    <fo:table-cell color=\"black\"\r\n                                                                            font-weight=\"bold\"\r\n                                                                            font-size=\"6pt\"\r\n                                                                            font-style=\"italic\"\r\n                                                                            border-top-color=\"black\"\r\n                                                                            border-top-style=\"dotted\"\r\n                                                                            border-top-width=\"1pt\">\r\n                                                                        <fo:block linefeed-treatment=\"preserve\"><xsl:value-of select=\"$upperRight\"/></fo:block>\r\n                                                                    </fo:table-cell>\r\n                                                                 </fo:table-row>\r\n                                                            </fo:table-body>\r\n                                                        </fo:table>\r\n                                                    </fo:block>\r\n                                                    <fo:block>\r\n                                                        <fo:table table-layout=\"fixed\">\r\n                                                            <fo:table-column column-width=\"25%\"/>\r\n                                                            <fo:table-column column-width=\"50%\"/>\r\n                                                            <fo:table-column column-width=\"25%\"/>\r\n                                                            <fo:table-body>\r\n                                                                <fo:table-row>\r\n                                                                    <fo:table-cell><fo:block/></fo:table-cell>\r\n                                                                    <fo:table-cell>\r\n                                                                        <fo:block text-align=\"center\" display-align=\"after\" margin-top=\"0mm\" margin-bottom=\"0mm\">\r\n                                                                            <fo:external-graphic\r\n                                                                                    src=\"url(\'{$baseURL}/moduleResources/idcards/card_logo_01.png\')\"\r\n                                                                                    scaling=\"uniform\"\r\n                                                                                    content-height=\"8mm\"\r\n                                                                                    content-width=\"scale-to-fit\"/>\r\n                                                                        </fo:block>\r\n                                                                    </fo:table-cell>\r\n                                                                    <fo:table-cell><fo:block/></fo:table-cell>\r\n                                                                </fo:table-row>\r\n                                                            </fo:table-body>\r\n                                                        </fo:table>\r\n                                                    </fo:block>\r\n                                                    <fo:block margin-top=\"0mm\" margin-bottom=\".5mm\">\r\n                                                        <fo:instream-foreign-object >\r\n                                                            <barcode:barcode\r\n                                                                xmlns:barcode=\"http://barcode4j.krysalis.org/ns\"\r\n                                                                message=\"{$identifier}\" orientation=\"0\">\r\n                                                                <barcode:code128>\r\n                                                                    <barcode:height>21mm</barcode:height>\r\n                                                                    <quiet-zone enabled=\"false\"></quiet-zone>\r\n                                                                    <barcode:checksum>ignore</barcode:checksum>\r\n                                                                    <barcode:module-width>0.55mm</barcode:module-width>\r\n                                                                    <barcode:human-readable>\r\n                                                                      <barcode:placement>bottom</barcode:placement>\r\n                                                                      <barcode:font-size>12pt</barcode:font-size>\r\n                                                                    </barcode:human-readable>\r\n                                                                </barcode:code128>\r\n                                                                <barcode:quiet-zone enabled=\"false\"/>\r\n                                                            </barcode:barcode>\r\n                                                        </fo:instream-foreign-object>\r\n                                                    </fo:block>\r\n                                                    <fo:block font-size=\"10pt\" font-weight=\"bold\">\r\n                                                        If found return to registration desk at nearest hospital.\r\n                                                    </fo:block>\r\n                                                </fo:block-container>\r\n                                            </fo:block-container>\r\n                                        </fo:block>\r\n                                    </fo:table-cell>\r\n                                </xsl:for-each>\r\n                            </fo:table-row>\r\n                        </xsl:for-each>\r\n                    </fo:table-body>\r\n                </fo:table>\r\n            </fo:block>\r\n        </fo:flow>\r\n      </fo:page-sequence>\r\n    </fo:root>\r\n  </xsl:template>\r\n</xsl:stylesheet>', '1', '2008-11-11 11:55:36', '1', '2009-05-18 15:55:55');
INSERT INTO `idcards_template` VALUES ('9', 'OpenMRS Card New/Empty (back)', 'Blank', 'Template to print the backside of ID Cards with newly created Patient Identifiers.  This page is mass printed by a print-shop and then \'front\' cards are printed on top later.', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\r\n<doc>\r\n    <!-- Important to keep baseURL element on one line with no spaces. -->\r\n    <baseURL>$!{baseURL}</baseURL>\r\n    <cardName>OpenMRS ID Card</cardName>\r\n    <page>\r\n        #foreach ($elem in $identifiers)\r\n            #if ($velocityCount < $identifiers.size())\r\n                #set ($mod = $velocityCount % 2)\r\n                <!-- print two columns -->\r\n                #if ($mod == 1)\r\n                    #set ($left = $velocityCount - 1)\r\n                    <row>\r\n                        <col>${identifiers.get($left)}</col>\r\n                        <col>${identifiers.get($velocityCount)}</col>\r\n                    </row>\r\n                #else\r\n                    <!-- print one column for the last element -->\r\n                    #if ($velocityCount == $identifiers.size() - 1)\r\n                        #set ($last = $identifiers.size() - 1)\r\n                        <row>\r\n                            <col>${identifiers.get($last)}</col>\r\n                        </row>\r\n                    #end\r\n                #end\r\n            #end\r\n        #end\r\n    </page>\r\n</doc>', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\r\n<xsl:stylesheet version=\"2.0\"\r\n      xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\"\r\n      xmlns:fo=\"http://www.w3.org/1999/XSL/Format\"\r\n      xmlns:fn=\"http://www.w3.org/2005/02/xpath-functions\">\r\n  <xsl:output method=\"xml\" indent=\"yes\"/>\r\n\r\n  <xsl:template match=\"/\">\r\n    <fo:root>\r\n      <fo:layout-master-set>\r\n        <fo:simple-page-master master-name=\"simple\" page-height=\"297mm\" page-width=\"210mm\"\r\n            margin-top=\"0.6cm\" margin-left=\"0cm\" margin-right=\"0cm\" margin-bottom=\"0.8cm\">\r\n            <fo:region-body margin-top=\"0.4cm\" margin-bottom=\"0cm\" />\r\n        </fo:simple-page-master>\r\n      </fo:layout-master-set>\r\n      <fo:page-sequence master-reference=\"simple\">\r\n        <fo:flow flow-name=\"xsl-region-body\" font-family=\"Helvetica\" font-size=\"8pt\">\r\n            <fo:block text-align=\"center\">\r\n                <fo:table table-layout=\"fixed\" start-indent=\"(100% - 168mm - 3mm) div 2\"\r\n                        table-omit-header-at-break=\"false\">\r\n                    <fo:table-column column-width=\"84mm\"/>\r\n                    <fo:table-column column-width=\"84mm\"/>\r\n\r\n                    <fo:table-header border=\"solid white\" >\r\n                        <fo:table-row height=\"6mm\" >\r\n                            <fo:table-cell>\r\n                                <fo:block font-size=\"6pt\" font-weight=\"bold\" display-align=\"before\">\r\n                                    <xsl:value-of select=\"doc/cardName\"/> (<xsl:value-of select=\"doc/baseURL\"/>)\r\n                                </fo:block>\r\n                            </fo:table-cell>\r\n                            <fo:table-cell>\r\n                                <fo:block font-size=\"6pt\" font-weight=\"bold\">\r\n                                    Page: <fo:page-number/>\r\n                                </fo:block>\r\n                            </fo:table-cell>\r\n                        </fo:table-row>\r\n                    </fo:table-header>\r\n\r\n                    <fo:table-body>\r\n                        <xsl:variable name=\"baseURL\" select=\"doc/baseURL\"/>\r\n                        <!-- xsl:variable name=\"contextPath\" select=\"idCardList/contextPath\" -->\r\n                        <xsl:for-each select=\"doc/page/row\">\r\n                            <fo:table-row height=\"52mm\">\r\n                                <xsl:for-each select=\"col\">\r\n                                    <xsl:variable name=\"id\" select=\".\"/>\r\n                                    <fo:table-cell border=\"2pt solid black\" display-align=\"before\">\r\n                                        <fo:block text-align=\"center\" start-indent=\"0mm\" margin-top=\"2mm\" font-weight=\"bold\">\r\n                                            <fo:block-container>\r\n                                                <fo:block-container position=\"absolute\">\r\n                                                    <fo:block>\r\n                                                        <fo:table table-layout=\"fixed\">\r\n                                                            <fo:table-column column-width=\"100%\"/>\r\n                                                            <fo:table-body>\r\n                                                                <fo:table-row>\r\n                                                                    <fo:table-cell>\r\n                                                                        <fo:block color=\"black\" font-weight=\"bold\" >\r\n                                                                            <fo:table table-layout=\"fixed\">\r\n                                                                                <fo:table-column column-width=\"10%\"/>\r\n                                                                                <fo:table-column column-width=\"30%\"/>\r\n                                                                                <fo:table-column column-width=\"25%\"/>\r\n                                                                                <fo:table-column column-width=\"25%\"/>\r\n                                                                                <fo:table-column column-width=\"10%\"/>\r\n                                                                                <fo:table-body>\r\n                                                           