INSERT INTO `idcards_template` VALUES ('10', 'OpenMRS Card Reprint', 'Reprint', 'Template to reprint Patient ID Cards for existing patients.  (cards are cut-out, folded over, and put into a sleeve)', '<?xml version=\"1.0\"?>\r\n<idCardList>\r\n    <baseURL>$!{baseURL}</baseURL>\r\n    <contextPath>$!{baseURL}</contextPath>\r\n    <cardName>OpenMRS Card Reprint</cardName>\r\n#foreach($patientId in $cohort.memberIds)\r\n$!{fn.setPatientId($patientId)}\r\n<idCard>\r\n	<id>$!{fn.getPatientAttr(\'PatientIdentifier\', \'identifier\')}</id>\r\n	<name>\r\n		$!{fn.getPatientAttr(\'PersonName\', \'givenName\')}\r\n		$!{fn.getPatientAttr(\'PersonName\', \'middleName\')}\r\n		$!{fn.getPatientAttr(\'PersonName\', \'familyName\')}\r\n	</name>\r\n	<gender>$!{fn.getPatientAttr(\'Person\', \'gender\')}</gender>\r\n    #set($birthdate = $!{fn.getPatientAttr(\'Person\', \'birthdate\')})\r\n    #set($birthdate_estimated = $!{fn.getPatientAttr(\'Person\', \'birthdateEstimated\')})\r\n    #if ($birthdate)\r\n        #set($age = $!{fn.getAge(${birthdate})})\r\n        #if ($!{age.get(1)} == \"days\")\r\n            #set($age_text = \"$!{age.get(0)} day-old\")\r\n        #elseif ($!{age.get(1)} == \"weeks\")\r\n            #set($age_text = \"$!{age.get(0)} week-old\")\r\n        #elseif ($!{age.get(1)} == \"months\")\r\n            #set($age_text = \"$!{age.get(0)} month-old\")\r\n        #else\r\n            #set($age_text = \"$!{age.get(0)} year-old\")\r\n        #end\r\n    #else\r\n        #set($age_text = \"??? year-old\")\r\n    #end\r\n    #if ($!{birthdate_estimated} == \'true\') #set ($approx = \"~ \")\r\n    #else #set ($approx = \" \")\r\n    #end\r\n	<birthdate estimate=\"${birthdate_estimated}\" age=\"$!{age_text}\">\r\n		 $!{approx}$!{fn.formatDate(\'dd-MMM-yyyy\',${birthdate})}</birthdate>\r\n    <phone>$!{fn.getPersonAttribute(\'Contact Phone Number\')}</phone>\r\n    #set ($prevIds = $!{fn.getPatient().getIdentifiers()})\r\n    <prevIds>\r\n        #foreach($prevId in $prevIds)\r\n           #if($prevId.getIdentifier() != $!{fn.getPatientAttr(\'PatientIdentifier\', \'identifier\')} ) ${prevId.getIdentifier()}#if($velocityCount < $prevIds.size()), #end#end\r\n        #end\r\n    </prevIds>\r\n    <upperLeft>Date Issued</upperLeft>\r\n    <upperRight>Assigning Location</upperRight>\r\n</idCard>\r\n#end <!-- ending patient loop -->\r\n</idCardList>', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\r\n<!DOCTYPE xsl:stylesheet [\r\n<!ENTITY asymptotic-to \"&#x2248;\">\r\n]>\r\n<xsl:stylesheet version=\"2.0\"\r\n      xmlns:xsl=\"http://www.w3.org/1999/XSL/Transform\"\r\n      xmlns:fo=\"http://www.w3.org/1999/XSL/Format\"\r\n      xmlns:fn=\"http://www.w3.org/2005/02/xpath-functions\">\r\n  <xsl:output method=\"xml\" indent=\"yes\"/>\r\n\r\n  <xsl:template match=\"/\">\r\n    <fo:root>\r\n      <fo:layout-master-set>\r\n        <fo:simple-page-master master-name=\"simple\" page-height=\"297mm\" page-width=\"210mm\"\r\n            margin-top=\"0.6cm\" margin-left=\"0cm\" margin-right=\"0cm\" margin-bottom=\"0.8cm\">\r\n            <fo:region-body margin-top=\"0.4cm\" margin-bottom=\"0cm\" />\r\n        </fo:simple-page-master>\r\n      </fo:layout-master-set>\r\n      <fo:page-sequence master-reference=\"simple\">\r\n        <fo:flow flow-name=\"xsl-region-body\" font-family=\"Helvetica\" font-size=\"8pt\">\r\n            <fo:block text-align=\"center\">\r\n                <fo:table table-layout=\"fixed\" start-indent=\"(100% - 168mm - 3mm) div 2\"\r\n                        table-omit-header-at-break=\"false\" >\r\n                    <fo:table-column column-width=\"84mm\"/>\r\n                    <fo:table-column column-width=\"84mm\"/>\r\n\r\n                    <fo:table-header border=\"solid white\" >\r\n                        <fo:table-row height=\"6mm\" >\r\n                            <fo:table-cell>\r\n                                <fo:block font-size=\"6pt\" font-weight=\"bold\" display-align=\"before\">\r\n                       