barcode:quiet-zone>\r\n                                                                    <barcode:checksum>ignore</barcode:checksum>\r\n                                                                    <barcode:module-width>0.55mm</barcode:module-width>\r\n                                                                    <barcode:human-readable>\r\n                                                                      <barcode:placement>bottom</barcode:placement>\r\n                                                                      <barcode:font-size>12pt</barcode:font-size>\r\n                                                                    </barcode:human-readable>\r\n                                                                </barcode:code128>\r\n                                                                <barcode:quiet-zone enabled=\"false\"/>\r\n                                                            </barcode:barcode>\r\n                                                        </fo:instream-foreign-object>\r\n                                                    </fo:block>\r\n                                                    <fo:block font-size=\"10pt\" font-weight=\"bold\">\r\n                                                        If found return to registration desk at nearest hospital.\r\n                                                    </fo:block>\r\n                                                </fo:block-container>\r\n                                            </fo:block-container>\r\n                                        </fo:block>\r\n                                    </fo:table-cell>\r\n                                    <fo:table-cell border=\"1pt dashed grey\" display-align=\"before\">\r\n                                        <fo:block text-align=\"center\" start-indent=\"0mm\" margin-top=\"5mm\">\r\n                                            <fo:block-container>\r\n                                             